document.addEventListener("DOMContentLoaded",function(){(function(){const getAccordion=document.querySelectorAll(".js-accordion");for(let i=0;i<getAccordion.length;i+=1){getAccordion[i].addEventListener("click",function(e){if(this.classList.contains("accordion-active")===false){this.classList.add("accordion-active")}else if(this.classList.contains("accordion-active")===true){this.classList.remove("accordion-active")}e.stopPropagation()})}})();(function(){const getDropdown=document.querySelectorAll(".js-dropdown");for(let i=0;i<getDropdown.length;i+=1){getDropdown[i].addEventListener("click",function(e){if(this.classList.contains("dropdown-active")===false){this.classList.add("dropdown-active")}else if(this.classList.contains("dropdown-active")===true){this.classList.remove("dropdown-active")}e.stopPropagation()})}document.addEventListener("click",function(e){if(e.target!==getDropdown){for(let i=0;i<getDropdown.length;i+=1){getDropdown[i].classList.remove("dropdown-active")}}})})();(function(){const intLinks=document.querySelectorAll("a[href^='#']");for(let i=0;i<intLinks.length;i+=1){intLinks[i].addEventListener("click",function(e){e.preventDefault();document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})}})();});
